import React, { useState } from 'react';
import ExpensesFilter from '../Filter/ExpensesFilter';
import ExpensesList from './ExpensesList';
import Card from '../UI/Card';
import './Expenses.css';

const Expenses = (props) => {

    const [selectedDate, dateFilterSelected] = useState('2020');

    const filterChangeHandler = selectedYear => {
        dateFilterSelected(selectedYear);
    }

    const filteredExpenses = props.items.filter(expense => {
        return expense.date.getFullYear().toString() === selectedDate;
    })

    const [buttonClicked, addExpense] = useState('');

    const addExpenseButtonHandler = (event) => {
        console.log("Button Clicked");
        addExpense("clicked");
    }

    if (buttonClicked === "clicked") {
        return (
            <Card className="expenses">
                <ExpensesFilter
                    selected={selectedDate}
                    onChangeFilter={filterChangeHandler}
                />
                <ExpensesList items={filteredExpenses} />

                <button type="submit" onClick={addExpenseButtonHandler}> Add New Expense</button>

            </Card>
        )
    }

    return (
        <Card className="expenses">
            <ExpensesFilter
                selected={selectedDate}
                onChangeFilter={filterChangeHandler}
            />
            <ExpensesList items={filteredExpenses} />

            <button type="submit" onClick={addExpenseButtonHandler}> Add New Expense</button>

        </Card>
    );
}

export default Expenses;
