import React, { useState } from 'react';

import Expenses from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense';

const DUMMY_EXPENSES = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {

  const [expenses, setExpense] = useState(DUMMY_EXPENSES);

  // return React.createElement(
  //   'div',
  //   {},
  //   React.createElement('h2', {}, "Let's get started!"),
  //   React.createElement(Expenses, { items: expenses })
  // );

  const addExpenseHandler = expense => {
    setExpense((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  }

  const [buttonClicked, addExpense] = useState('');

  // const addExpenseFormHandler = () => {
  //   console.log("In App -> Button Clicked");
  //   addExpense("Clicked");
  // }

  const addExpenseButtonHandler = (event) => {
    // console.log("Button Clicked");
    console.log("In App -> Button Clicked");
    addExpense("clicked");
  }

  if (buttonClicked === "Clicked") {
    console.log("Yes it is clicked.");

    return (
      <div>
        <NewExpense
          onAddExpense={addExpenseHandler}
          addExenseForm={addExpenseFormHandler}
        />
        <Expenses items={expenses} />
      </div>
    )
  }

  return (
    <div>
      <button type="submit" onClick={addExpenseButtonHandler}> Add New Expense</button>
      <NewExpense
        onAddExpense={addExpenseHandler}
        addExenseForm={addExpenseFormHandler}
      />
      <Expenses items={expenses} />
    </div>
  );
}

export default App;
