//import React, { useState } from 'react';
import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {

    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString() // needs to be change to avoid duplicates
        };
        props.onAddExpense(expenseData);
    }

    //const [buttonClicked, addExpense] = useState('');

    // const addExpenseButtonHandler = (event) => {
    //     // console.log("Button Clicked");
    //     props.addExenseForm("clicked");
    // }
    const buttonClicked = () => {
        props.addExenseForm();
    }

    return <div className="new-expense">
        {/* onSaveExpenseData is the props name and saveExpenseDataHandler is the function called on it. 
        We then pass then  into the ExpenseForm Component to deal with the user input*/}
        {/* <button type="submit" onClick={addExpenseButtonHandler}> Add New Expense</button> */}
        <ExpenseForm
            onSaveExpenseData={saveExpenseDataHandler}
            sendClick={buttonClicked}
        />

    </div>
}

export default NewExpense;
