import React, { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = (props) => {

    // Adding a state change to Title input in the form
    const [enterTitle, setEnteredTitle] = useState('');
    // This is th function that is called when there is a change in the input field
    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }
    // Adding a state change to amount input in the form
    const [enteredAmount, setAmount] = useState('');
    // This is th function that is called when there is a change in the input field
    const amountChangeHandler = (event) => {
        setAmount(event.target.value);
    }
    // Adding a state change to date input in the form
    const [enteredDate, setDate] = useState('');
    // This is th function that is called when there is a change in the date field
    const dateChangeHandler = (event) => {
        setDate(event.target.value);
    }

    const submitHandle = (event) => {
        event.preventDefault(); // prevents the form from causing a page reload

        const expenseData = {
            title: enterTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        // enteredTitle, enterAmount, enteredDate are the state variables.
        props.onSaveExpenseData(expenseData);

        // Lets clear the form
        setEnteredTitle('');
        setAmount('');
        setDate('');
    };
    const buttonClicked = () => {
        props.addExenseForm();
    }

    return (
        <form onSubmit={submitHandle}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' value={enterTitle} onChange={titleChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' min='0.01' step="0.01" value={enteredAmount} onChange={amountChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' min="2019-01-01" max="2022-2-31" value={enteredDate} onChange={dateChangeHandler} />
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
                <button type="submit" onClick={buttonClicked}>Cancel</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
