import React, { useState } from 'react';
import AddUser from './components/AddUser/AddUser';
import './App.css';
import UserList from './components/UserList/UserList';

function App() {
  const [users, setUsers] = useState("");
  // [{ name: 'Do all exercises!', age: '41', id: 'g1' }]

  const addUserHandler = enteredText => {
    setUsers(prevGoals => {
      const updatedGoals = [...prevGoals];
      updatedGoals.unshift({ name: enteredText, id: Math.random().toString() });
      return updatedGoals;
    });
  };

  let content = (
    <p style={{ textAlign: 'center' }}>No goals found. Maybe add one?</p>
  );

  if (users.length > 0) {
    content = (
      <UserList items={users} />
    );
  }

  return (
    <div>
      <div id="goal-form">
        <AddUser onAddGoal={addUserHandler} />
      </div>
      <section id="goals">
        {content}
      </section>
    </div>
  );
}

export default App;
