import React, { useState } from 'react';
import Button from '../Button/Button';

const AddUser = (props) => {

    const [enteredNameValue, setNameValue] = useState('');

    const [enteredAgeValue, setAgeValue] = useState('');

    const [isValid, setIsValid] = useState(true);

    const formSubmitHandler = event => {
        event.preventDefault();
        if (enteredNameValue.trim().length === 0) {
            setIsValid(false);
            return;
        }
        props.onAddGoal(enteredNameValue, enteredAgeValue);
    };

    const ageInputChangeHandler = event => {
        if (event.target.value.trim().length > 0) {
            setIsValid(true);
        }
        setAgeValue(event.target.value);
    };

    const nameInputChangeHandler = event => {
        if (event.target.value.trim().length > 0) {
            setIsValid(true);
        }
        setNameValue(event.target.value);
    };

    return (
        <form onSubmit={formSubmitHandler}>
            <div className="form-control" >
                <div>
                    <label>User Name</label>
                    <input type="text" onChange={nameInputChangeHandler} />
                </div>

                <div>
                    <label>Age (Years)</label>
                    <input type="text" onChange={ageInputChangeHandler} />
                </div>
            </div>
            <Button type="submit">Add User</Button>
        </form >
    );
}

export default AddUser;
